import React from 'react'
import '../Styles/PhotoProjectsList.css';
import {useRef} from 'react';

import buildinGoneVer from '../assets/Dec21-22(2022)/img006.png'
import easternBuiVer  from '../assets/Dec21-22(2022)/img0009.png'
import fourthStBridgeVer from '../assets/Dec21-22(2022)/img016.png'

import {useState} from 'react';
import ImageGrid from './ImageGrid';




function ProjectsList() {

    const [isShown, setIsShown] = useState(false);

    const handleClick = event => {
      // üëáÔ∏è toggle shown state
      setIsShown(current => !current);

      // üëáÔ∏è or simply set it to true
      setIsShown(true);
    //   handleDown()
    };

    function print(){
        console.log("test")
    }



    // const ref = React.forwardRef(null);


    // React.forwardRef()


    // const handleDown = () => {
    //   ref.current?.scrollIntoView({behavior: 'smooth'});
    // };

    const handleClickScroll = () => {
        const element = document.getElementById('section-1');
        if (element) {
          // üëá Will scroll smoothly to the top of the next section
          element.scrollIntoView({ behavior: 'smooth' });
        }
      };

    return (
        <div className='PhotoList-main'>

            <div className=''>

            </div>
            <div className="grid-box">
                <div className="grid-showcase-item">
                    <button onClick={handleClick} className="button-project">
                        <img
                            className='images'
                            src={easternBuiVer}
                            alt="" 
                            style={{ height:"100%",objectFit: "cover"}}
                        />
                        <p className='titleOfProject'>All Photos</p>
                    </button>
                </div>
                <div className="grid-showcase-item">
                    <button onClick={print} className="button-project">
                        <img
                            className='images'
                            src={buildinGoneVer}
                            alt="" 
                            style={{ height:"100%",objectFit: "cover"}}
                        />
                        <p className='titleOfProject'>B&W</p>
                    </button>
                </div>
                <div className="grid-showcase-item">
                    <button onClick={print} className="button-project">
                        <img
                            className='images'
                            src={fourthStBridgeVer}
                            alt="" 
                            style={{ height:"100%",objectFit: "cover"}}
                        />
                        <p className='titleOfProject'>Color</p>
                    </button>
                </div>  
            </div>



            {/* <button onClick={handleClick}>Click</button> */}
            {/* üëáÔ∏è show elements on click */}
            {isShown && (
            <div>
                {isShown && <ImageGrid id="section-1"/>}
            </div>
            )}
            {/* üëáÔ∏è show component on click */}
        </div>
    )
}

function Box() {
    return (
      <div>
        <h2>Box</h2>
      </div>
    );
  }

export default ProjectsList
